<?php

class block_online_class extends block_base {

    public function init() {
        $this->title = get_string('online_class', 'block_online_class');
    }

    function hide_header(): bool
    {
        return true;
    }

    function allow_multiple(): bool
    {
        return false;
    }

    public function get_content() {
        if ($this->content !== null) {
            return $this->content;
        }

        $button_block = "
            <div class=\"button-block inactive\">
                <button>Смотреть</button>
            </div>
        ";
        $svg_image = $this->get_svg_image();

        // Generate actual online class url.
        global $DB, $USER;
        $sql_query = "
            SELECT * FROM {logstore_standard_log} WHERE
            action = ?  AND target = ? AND userid = ? AND courseid != 1
            order by timecreated desc
        ";
        $lastcourse = $DB->get_records_sql($sql_query, array('viewed', 'course', $USER->id), 0, 1);
        $lastcourse = count($lastcourse) > 0 ? intval(reset($lastcourse)->courseid) : 0;
        if ($lastcourse > 1) {
            $jitsi = intval($DB->get_record('modules', array('name' => 'jitsi'))->id);
            $onlineclass = $DB->get_record('course_modules', array('course' => $lastcourse, 'module' => $jitsi));
            if (!empty($onlineclass)) {
                $onlineclassurl = new moodle_url('/mod/jitsi/view.php', array('id' => $onlineclass->id));
                $button_block = "
                    <div class=\"button-block\">
                        <button onclick=\"location.href='$onlineclassurl'\">Смотреть</button>
                    </div>
                ";
            }
        }

        // Widget content.
        $this->content = new stdClass;
        $this->content->text = "
            <div class=\"online-class\">
                <div class=\"online-class-body\">
                    <div class=\"block-title\">Виртуальный<br>класс</div>
                    <div class=\"data-line\">
                        <div class=\"data-text\"></div>
                    </div> 
                        $button_block
                </div>
                <div class=\"online-class-image\">
                    $svg_image
                </div>
            </div>
        ";

        /*
        <div class=\"data-line\">
                        <div class=\"data-text\">продолжительность:</div>
                        <div class=\"data-value\">1 час</div>
                    </div>
                    <div class=\"data-line\">
                        <div class=\"data-text\">закончится в:</div>
                        <div class=\"data-value\">14:00</div>
                    </div>        
        */
        
        return $this->content;
    }

